function createRectangle(parent, color, x, y) {
    var g = $.createShape({parent: parent, lifeTime:0, x:0, y:0, name:"bg"});
    g.graphics.beginFill(color);
    g.graphics.drawRect(0,0,x,y);
    g.graphics.endFill();
    return g;
}

