/*************************************** diaglog functions ****************************/
function showPanel() {
    var textfield = Global.CANVAS.DIALOG_CANVAS.getChildByName('msg');
    textfield.text = "";
    Global.CANVAS.DIALOG_CANVAS.alpha = 1.;
}

function hidePanel() {
    var textfield = Global.CANVAS.DIALOG_CANVAS.getChildByName('msg');
    textfield.text = "";
    Global.CANVAS.DIALOG_CANVAS.alpha = 0.;
}

function dialog(text, yes_callback, no_callback) {
    var textfield = Global.CANVAS.DIALOG_CANVAS.getChildByName('msg');
    textfield.text = text;
    if (textfield.width > maxwidth)
    {
        textfield.multiline = true;
        textfield.wordWrap = true;
        textfield.width = maxwidth;
    }
    /*ScriptManager.clearEl();*/
    var yes_but = Global.CANVAS.DIALOG_CANVAS.getChildByName('yes_but');
    if (yes_callback != null) {
        yes_but.alpha = 1.;
        Global.SPECIAL.YES_CALLBACK = yes_callback;
    } else {
        yes_but.alpha = 0;
        Global.SPECIAL.YES_CALLBACK = function () {};
    }
    var no_but = Global.CANVAS.DIALOG_CANVAS.getChildByName('no_but');
    if (no_callback != null) {
        no_but.alpha = 1.;
        Global.SPECIAL.NO_CALLBACK = no_callback;
    } else {
        no_but.alpha = 0;
        Global.SPECIAL.NO_CALLBACK = function () {};
    }
}

function createDialogRecur(arr) {
    if (arr.length == 0) {
        hidePanel();
        Global.KEY_BLOCKED = false;
        return;
    }
    var d = arr[0];
    arr.splice(0, 1);
    var yes_call = d.length > 1 ? function () {
        (d[1])();
        createDialogRecur(arr);
    } : function () {
        createDialogRecur(arr);
    };
    var no_call = d.length > 2 ?  function () {
        (d[2])();
        createDialogRecur(arr);
    } : function () {
        createDialogRecur(arr);
    };
    dialog(d[0], yes_call, no_call);
}

function createDialogStrip(arr) {
    Global.KEY_BLOCKED = true;
    showPanel();
    createDialogRecur(arr);
}
/*************************************** diaglog functions end***************************/

